$length= @SID;
# $length=1;


	for($TT=0; $TT<$length; $TT+=1)
	{			
	$ID=@SID[$TT];
		
	$name = "snotel_";
	$snotel = "bcqc_";
	$period = ".";
	$under = "_";
	$nc = ".nc";
	$dom = "_domain";
	$sta = "_state";
	$for = "_forcing";

	$file = $snotel . $ID . ".conf";
	$fileout = $name . $ID;
	
	$domain = $snotel . $ID . $dom . $nc;
	$state = $snotel . $ID . $sta . $nc;
	$forcing = $snotel . $ID . $for . $nc;
	
	open(CODE, ">$file");
	print CODE <<"END";
    
# This is an example of an input file for MetSim
[MetSim]
#out_vars = ['temp', 'prec', 'shortwave', 'longwave', 'vapor_pressure', 'rel_humid', 'air_pressure', 'wind']
out_vars = ['temp', 'prec', 'shortwave', 'longwave', 'vapor_pressure', 'rel_humid', 'air_pressure']

# Time step in minutes
time_step = 60

# Forcings begin here (year/month/day:hour) (hour optional)
start = 2017/4/1
; start = 2018/12/31

# Forcings end at this date (year/month/day)
stop = 2018/8/30
; stop = 2018/8/31

# Input and output directories
; forcing = ../metsim/data/test.nc
forcing = $forcing
; domain  = ../metsim/data/domain.nc
domain  = $domain
; state = ../metsim/data/state_nc.nc
state = $state

forcing_fmt = netcdf
in_format = netcdf

out_dir = 
out_prefix = $fileout
; out_freq = 1

; prec_type = triangle
prec_type = uniform
utc_offset = False

[chunks]
lat = 1
lon = 1

[forcing_vars]
prec  = Prec
t_max = Tmax
t_min = Tmin
# wind  = wind

[state_vars]
prec  = prec
t_max = t_max
t_min = t_min

[domain_vars]
lat  = lat
lon  = lon
mask = mask
elev = elev
; t_pk = t_pk
; dur  = dur
  		
END
close(CODE);

